import React from 'react';
import './About.css'; // Assuming you have a separate CSS file for styling

const About = () => {
    return (
        <div className="about-container">
            <h1>About Govind Toys</h1>
            <p>
                Welcome to Govind Toys, your number one source for all things toys. We're dedicated to giving you the very best of toys, with a focus on dependability, customer service, and uniqueness.
            </p>
            <p>
                Founded in 2023, Govind Toys has come a long way from its beginnings. When we first started out, our passion for toys drove us to do tons of research, so that Govind Toys can offer you the world's most advanced toys. We now serve customers all over the country and are thrilled that we're able to turn our passion into our own website.
            </p>
            <p>
                We hope you enjoy our products as much as we enjoy offering them to you. If you have any questions or comments, please don't hesitate to contact us.
            </p>
        </div>
    );
};

export default About;
